lueString($_POST['answerText3'], "text"),                       GetSQLValueString($_POST['questionText3'], "text"),					   GetSQLValueString($_POST['tutorialName'], "text"),					   GetSQLValueString(session_id(), "text"));					     mysql_select_db($database_beSocratic, $beSocratic);  $Result1 = mysql_query($insertSQL, $beSocratic) or die(mysql_error());    if (!isset($_COOKIE["PHPSESSID"])) {	  $_SERVER['QUERY_STRING'] = htmlspecialchars(session_name()."=".session_id());  } else {	  $_SERVER['QUERY_STRING'] = "";  }  $insertGoTo = "tutorial5.php";  if (isset($_SERVER['QUERY_STRING'])) {    $insertGoTo .= (strpos($insertGoTo, '?')) ? "&" : "?";    $insertGoTo .= $_SERVER['QUERY_STRING'];  }  header(sprintf("Location: %s", $insertGoTo));}?><!doctype html public "-//w3c//dtd html 4.0 transitional//en"><html><head>   <meta http-equiv="Content-Type" content="text/html; charset=ISO-8859-1">   <meta name="Content-Type" content="text/html; charset=iso-8859-1">   <meta name="description" content="BeSocratic (be socratic) tutorials in the sciences">   <meta name="keywords" content="Klymkowsky, Boulder, BeSocratic, be socratic, molecular biology, evolutionary biology, chemistry & physics">   <meta name="GENERATOR" content="Mozilla/4.75 (Macintosh; U; PPC) [Netscape]">   <meta name="KeyWords" content="Klymkowsky, BeSocratic, Teaching and learning science">   <title>BeSocratic - Graphical Thinking</title>   <script src="../../../../CLUE/StudentMaterials/chapter-1/storage/Scripts/AC_RunActiveContent.js" type="text/javascript"></script>   <script src="../../../../CLUE/StudentMaterials/chapter-1/Scripts/swfobject_modified.js" type="text/javascript"></script><meta name="keywords" content="Mendel and Muller, mutations and phenotypes"><style type="text/css"><!--.arial {	font-family: Arial, Helvetica, sans-serif, Optima;}.arial {	font-family: Arial, Helvetica, sans-serif;}--></style></head><style type="text/css"><!--.unnamed1 {  background-c